package com.hkd.ithome.bean;public class NewsModel {	int id;	int pageIndex;	String img;	String title;	String pinglun;	String time;	String typeId;	String link;	public int getId() {		return id;	}	public void setId(int id) {		this.id = id;	}	public int getPageIndex() {		return pageIndex;	}	public void setPageIndex(int pageIndex) {		this.pageIndex = pageIndex;	}	public String getImg() {		return img;	}	public void setImg(String img) {		this.img = img;	}	public String getTitle() {		return title;	}	public void setTitle(String title) {		this.title = title;	}	public String getPinglun() {		return pinglun;	}	public void setPinglun(String pinglun) {		this.pinglun = pinglun;	}	public String getTime() {		return time;	}	public void setTime(String time) {		this.time = time;	}	public String getTypeId() {		return typeId;	}	public void setTypeId(String typeId) {		this.typeId = typeId;	}	public String getLink() {		return link;	}	public void setLink(String link) {		this.link = link;	}	}